var components = {"appendedcheckbox":"<!-- Appended checkbox -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <div class=\"input-append\">\n      <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"span2\" type=\"text\" placeholder=\"<%= placeholder %>\" <% if(required) {%> required <% } %> />\n      <span class=\"add-on\">\n        <input type=\"checkbox\"<% if (checked){ %> checked <% } %>>\n      </span>\n    </div>\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","appendedtext":"<!-- Appended Input-->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <div class=\"input-append\">\n      <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\" placeholder=\"<%= placeholder %>\" type=\"text\"<% if(required) {%> required <% } %> />\n      <span class=\"add-on\"><%= append %></span>\n    </div>\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","button":"<!-- Button -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <button id=\"<%= id %>\" name=\"<%= id %>\" class='btn <%= buttontype %>'><%= buttonlabel %></button>\n  </div>\n</div>\n","buttondouble":"<!-- Button (Double) -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <button id=\"<%= id %>\" name=\"<%= id %>\" class='btn <%= button1type %>'><%= button1label %></button>\n    <button id=\"<%= id2 %>\" name=\"<%= id2 %>\" class='btn <%= button2type %>'><%= button2label %></button>\n  </div>\n</div>\n","buttondropdown":"<!-- Button Drop Down -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <div class=\"input-append\">\n      <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\" placeholder=\"<%= placeholder %>\" type=\"text\" <% if(required) {%> required <% } %> />\n      <div class=\"btn-group\">\n        <button class=\"btn dropdown-toggle\" data-toggle=\"dropdown\">\n          <%= buttontext %>\n          <span class=\"caret\"></span>\n        </button>\n        <ul class=\"dropdown-menu\"><% _.each(buttonoptions, function(value) { %>\n          <li><a href=\"#\"><%= value %></a></li><% }); %>\n        </ul>\n      </div>\n    </div>\n  </div>\n</div>\n","filebutton":"<!-- File Button --> \n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"input-file\" id=\"fileInput\" type=\"file\">\n  </div>\n</div>\n","formname":"<!-- Form Name -->\n<legend><%= name %></legend>\n","multiplecheckboxes":"<!-- Multiple Checkboxes -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= name %>\"><%= label %></label>\n  <div class=\"controls\"><% _.each(checkboxes, function(checkbox, i){ %>\n    <label class=\"checkbox\" for=\"<%= name+'-'+i %>\">\n      <input type=\"checkbox\" name=\"<%= name %>\" id=\"<%= name+'-'+i %>\" value=\"<%= checkbox %>\"<% if(required){ %> required=\"required\"<% } %>>\n      <%= checkbox %>\n    </label><% }); %>\n  </div>\n</div>\n","multiplecheckboxesinline":"<!-- Multiple Checkboxes (inline) -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= name %>\"><%= label %></label>\n  <div class=\"controls\"><% _.each(checkboxes, function(checkbox, i){ %>\n    <label class=\"checkbox inline\" for=\"<%= name+'-'+i %>\">\n      <input type=\"checkbox\" name=\"<%= name %>\" id=\"<%= name+'-'+i %>\" value=\"<%= checkbox %>\"<% if(required){ %> required=\"required\"<% } %>>\n      <%= checkbox %>\n    </label><% }); %>\n  </div>\n</div>\n","multipleradios":"<!-- Multiple Radios -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= name %>\"><%= label %></label>\n  <div class=\"controls\"><% _.each(radios, function(radio, i){ %>\n    <label class=\"radio\" for=\"<%= name+'-'+i %>\">\n      <input type=\"radio\" name=\"<%= name %>\" id=\"<%= name+'-'+i %>\" value=\"<%= radio %>\"  <% if (i < 1) { %> checked=\"checked\" <% } %><% if(required){ %> required=\"required\"<% } %>>\n      <%= radio %>\n    </label><% }); %>\n  </div>\n</div>\n","multipleradiosinline":"<!-- Multiple Radios (inline) -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= name %>\"><%= label %></label>\n  <div class=\"controls\"><% _.each(radios, function(radio, i){ %>\n    <label class=\"radio inline\" for=\"<%= name+'-'+i %>\">\n      <input type=\"radio\" name=\"<%= name %>\" id=\"<%= name+'-'+i %>\" value=\"<%= radio %>\" <% if(i < 1) { %>checked=\"checked\" <% } %><% if(required){ %> required=\"required\"<% } %>>\n      <%= radio %>\n    </label><% }); %>\n  </div>\n</div>\n","passwordinput":"<!-- Password input-->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <input id=\"<%= id %>\" name=\"<%= id %>\" type=\"password\" placeholder=\"<%= placeholder %>\" class=\"<%= inputsize %>\" <% if(required) {%> required <% } %> />\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","prependedcheckbox":"<!-- Prepended checkbox -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <div class=\"input-prepend\">\n      <span class=\"add-on\">\n        <label class=\"checkbox\">\n          <input type=\"checkbox\" <% if (checked){ %>checked=\"checked\"<% } %>>\n        </label>\n      </span>\n      <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\" type=\"text\" placeholder=\"<%= placeholder %>\" <% if(required) {%> required <% } %> />\n    </div>\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","prependedtext":"<!-- Prepended text-->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <div class=\"input-prepend\">\n      <span class=\"add-on\"><%= prepend %></span>\n      <input id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\" placeholder=\"<%= placeholder %>\" type=\"text\"<% if(required) {%> required <% } %> />\n    </div>\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","searchinput":"<!-- Search input-->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <input id=\"<%= id %>\" name=\"<%= id %>\" type=\"text\" placeholder=\"<%= placeholder %>\" class=\"<%= inputsize %> search-query\" <% if(required) {%> required <% } %> />\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n","selectbasic":"<!-- Select Basic -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <select id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\"><% _.each(options, function(option) { %>\n      <option><%= option %></option><% }); %>\n    </select>\n  </div>\n</div>\n","selectmultiple":"<!-- Select Multiple -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <select id=\"<%= id %>\" name=\"<%= id %>\" class=\"<%= inputsize %>\" multiple=\"multiple\"><% _.each(options, function(option) { %>\n      <option><%= option %></option><% }); %>\n    </select>\n  </div>\n</div>\n","textarea":"<!-- Textarea -->\n<div class=\"control-group\">\n  <label class=\"control-label\" for =\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">                     \n    <textarea id=\"<%= id %>\" name=\"<%= id %>\" ><%= textarea %></textarea>\n  </div>\n</div>\n","textinput":"<!-- Text input-->\n<div class=\"control-group\">\n  <label class=\"control-label\" for=\"<%= id %>\"><%= label %></label>\n  <div class=\"controls\">\n    <input id=\"<%= id %>\" name=\"<%= id %>\" type=\"text\" placeholder=\"<%= placeholder %>\" class=\"<%= inputsize %>\" <% if(required) {%> required <% } %> />\n    <% if (helptext.length > 0) { %><p class=\"help-block\"><%= helptext %></p><% } %>\n  </div>\n</div>\n"};